import "./Project.css"
import React from "react";

import { useState } from 'react'
import Slider from 'react-slick'
import astronaut from "../../assets/astronaut.png";
import celebrating from "../../assets/celebrating.png";
import education from "../../assets/education.png";
import taken from "../../assets/taken.png";
import { FaArrowRight, FaArrowLeft } from "react-icons/fa";

const images = [astronaut, celebrating, education, taken];

const Project = (props) => {



  const NextArrow = ({onClick}) => {
    return <div className="arrow next" onClick={onClick}>
      <FaArrowRight />
    </div>
  }

  const PrevArrow = ({onClick}) => {
    return <div className="arrow prev" onClick={onClick}>
      <FaArrowLeft />
    </div>
  }

  const [imageIndex, setImageIndex] = useState(0);

  const settings = {
    infinite: true,
    lazyload: true,
    speed: 300,
    slidesToShow: 3,
    centerMode: true,
    centerPadding: 0,
    nextArrow: <NextArrow />,
    prevArrow: <PrevArrow />,
    beforeChange: (curent, next) => setImageIndex(next)
  }

  return <div className="app">
    <Slider {...settings}>
      {images.map((img, idx) => {
        return <div className={idx === imageIndex ? "slide activeSlide" : "slide" }>
          <img src={img} alt={img} />
        </div>

      })}
    </Slider>
  </div>
};

export default Project;
